  return (
    <div>
      <h2>Phonebook</h2>
      <p>filter shown with</p>
      <input
        value={newName}
        onChange={handleNameChange}
      />
      <h2>Add a new</h2>
      <form onSubmit={addName}>
        <div>
          name:  
          <input 
            value={newName}
            onChange={handleNameChange}
          />
          <br></br>
          number:
          <input
            value={newPhoneNumber}
            onChange={handlePhoneNumberChange}
          />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      <table>
        <tbody>
          {persons.map(person =>
            <Person 
              key={person.id} 
              name={person.name} 
              number={person.number}
            />
          )}
        </tbody>
      </table>
    </div>
  )
}

export default App
