import React, { useState } from 'react'

const Person = ({name}) => {
  return (
    <tr>
      <td>
        {name}
      </td>
    </tr>)
}

// Check if the key and values og two objects 
const areThesePersonsEqual = (first, second) => {

  if (first.key === second.key) return true;
  if (first.name === second.name) return true;
  if (first.number === second.number) return true;

  return false;


  /*
  const al = Object.getOwnPropertyNames(first);
  const bl = Object.getOwnPropertyNames(second);

  // Check if the two list of keys are the same
  // length. If they are not, we know the objects
  // are not equal.
  if (al.length !== bl.length) return false;

  // Check that all keys from both objects match
  // are present on both objects.
  const hasAllKeys = al.every(value => !!bl.find(v => v === value));

  // If not all the keys match, we know the
  // objects are not equal.
  if (!hasAllKeys) return false;

  // We can now check that the value of each
  // key matches its corresponding key in the
  // other object.
  for (const key of al) if (first[key] !== second[key]) return false;
*/

}

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456', id: 1 },
    { name: 'Ada Lovelace', number: '39-44-5323523', id: 2 },
    { name: 'Dan Abramov', number: '12-43-234345', id: 3 },
    { name: 'Mary Poppendieck', number: '39-23-6423122', id: 4 }
  ])
  const [newName, setNewName] = useState('')
  const [newPhoneNumber, setNewPhoneNumber] = useState('')

  const addName = (event) => {
    var validInput = false
    var doesAlreadyExist = false

    event.preventDefault()

    if(newName !== '' && newPhoneNumber !== '') {
      validInput = true
    }

    if(validInput){

      const personObject = {
        name: newName,
        number: newPhoneNumber,
        id: persons.length + 1
      }

      persons.forEach(person => {
        doesAlreadyExist = areThesePersonsEqual(person, personObject)
      })

      if (!doesAlreadyExist) {
        setPersons(persons.concat(personObject))
        setNewName('')
        setNewPhoneNumber('')

      }else {
        alert(`${newName} is already added to phonebook`)
      }
    }
  }

  const handleNameChange = (event) => {
    setNewName(event.target.value)
  }

  const handlePhoneNumberChange = (event) => {
    setNewPhoneNumber(event.target.value)
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <form onSubmit={addName}>
        <div>
          name:  
          <input 
            value={newName}
            onChange={handleNameChange}
          />
          <br></br>
          number:
          <input
            value={newPhoneNumber}
            onChange={handlePhoneNumberChange}
          />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      <table>
        <tbody>
          {persons.map(person =>
            <Person key={person.id} name={person.name} />
          )}
        </tbody>
      </table>
    </div>
  )
}

export default App
